def llm_repair(state: 'PCTSPSolution', rng, **kwargs) -> 'PCTSPSolution':
    """
    Repair operator for the Price Collecting Travelling Salesman Problem (PCTSP) using ALNS.
    """
    unvisited = state.unvisited[:]

    # 1. Insert nodes based on a combination of prize and distance to nearest neighbor
    while unvisited:
        best_node = None
        best_score = -1

        for node in unvisited:
            # Find nearest neighbor in the current tour
            nearest_neighbor_dist = float('inf')
            if state.tour:
                for tour_node in state.tour:
                    dist = np.linalg.norm(DATA.locations[node - 1] - DATA.locations[tour_node - 1])
                    nearest_neighbor_dist = min(nearest_neighbor_dist, dist)
            else:
                nearest_neighbor_dist = np.linalg.norm(DATA.locations[node - 1] - DATA.depot)

            # Calculate a score based on prize and distance
            score = DATA.prizes[node - 1] / (nearest_neighbor_dist + 1e-6)  # Add a small epsilon to avoid division by zero

            if score > best_score:
                best_score = score
                best_node = node

        if best_node is not None:
            state.opt_insert(best_node)
            unvisited.remove(best_node)
        else:
            break

    # 2. Enforce prize constraint by iteratively adding nodes with the highest prize/penalty
    while not state.is_feasible():
        # Find the unvisited node with the highest prize-to-penalty ratio
        best_node = None
        best_ratio = -1

        for node in state.unvisited:
            ratio = DATA.prizes[node - 1] / (DATA.penalties[node - 1] + 1e-6)  # Avoid division by zero
            if ratio > best_ratio:
                best_ratio = ratio
                best_node = node

        if best_node is not None:
            state.opt_insert(best_node)
            state.unvisited.remove(best_node)
        else:
            # If no unvisited node can help, remove the lowest prize node
            worst_node = None
            worst_prize = float('inf')
            for node in state.tour:
                if DATA.prizes[node - 1] < worst_prize:
                    worst_prize = DATA.prizes[node - 1]
                    worst_node = node
            if worst_node is not None:
                state.remove(worst_node)
            else:
                break # No node can be removed

    return state
